css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.steampowered.com") {

.page { background:#FF3399 !important; }

HTML { background:#FF3399 !important; }

body { background:#FF3399 !important; }

.wysiwyg { background-color:#CC0066 !important; color:#FF3399 !important; background:#CC0066 !important; }



.alt1, .alt1Active, .alt2, .alt2Active { background:#CC0066 !important; border-color:#000 !important; color:#d0d0d0 !important; font-weight:normal !important; }

td.inlinemod { background:#CC0066 !important; }

.panel { background:#CC0066 !important }

.panelsurround { background:#CC0066 !important }

.thead { background:#CC0066 !important; border-color:#000 !important; }

.tfoot { background:#CC0066 !important; border-color:#000 !important; }

legend { background:#FF3399 !important; }

.tcat { background:#CC0066 !important; border-color:#000 !important; color:#d0d0d0 !important; font-weight:normal !important; }

.vbmenu_control a:link, .vbmenu_control_alink, .vbmenu_control a:visited, .vbmenu_control_avisited { background:#FF3399 !important; font-weight:normal !important; color:#d0d0d0 !important; }

#content { font-family:Calibri,Verdana,Arial,Helvetica,sans-serif !important; font-size-adjust:.6 !important; }

li { font-size:1em !important; line-height:1.5em !important; }

ol>li { list-style-type:decimal !important; }

}
