css:
@namespace url(http://www.w3.org/1999/xhtml);

/*

    Beautify2nite
    
    @author         s4nji
    @description    Beautifies several User Interface of die2nite
    @license        (CC)BY-SA
    @version        2
    
*/

/* Fix Broken Tabs */

@-moz-document domain("www.die2nite.com") {

#home_menu.tabs li a {
font-size: 11px !important;
line-height: 14px !important;
vertical-align: middle !important;
text-decoration: none !important;
padding: 0 4px!important;
position: relative !important;
top: 1px !important; }

#home_menu.tabs li a img {
position: relative !important;
top: 2px !important;
padding: 0 2px 0 0 !important; }

}

/* UI optimizations */

@-moz-document domain("www.die2nite.com") {

body:after {
opacity: 0;
content: '';
height: 0;
width: 0;
position: fixed;
left: 0;
top: 0;
z-index: 99999999999 !important;
background: rgba(0,0,0,0.5) !important;
-moz-transition: opacity 3000ms ease 0ms !important; }

body[style='cursor: progress;']:after {
opacity: 1;
width: 100%;
height: 100%; }

.table.select.large > tbody:first-child > tr:first-child > th[colspan='2'] {
padding-right: 9px !important; }

.table.select.large > tbody:first-child > tr:first-child > th[colspan='2']:after {
float: right;
margin-left: -4px;
content: 'H'; }

.table.select.large > tbody:first-child > tr:first-child > th[colspan='2'] + th + th {
opacity: 0;
width: 0; }

.table.select.large > tbody:first-child > tr:first-child > th[colspan='2'] + th + th + th:before {
content: "Defense" !important;
position: absolute;
margin-left: -80px; }

.home > img[src$='upgrade_none.gif'] + em:after {
content: '';
display: block;
position: absolute;
width: 80px;
margin-left: -5px;
margin-top: -21px;
height: 22px;
background: rgba(255,0,0,0.2); }

.name > img:last-child {
float: right !important; }

.name > img:first-child {
display: block !important;
float: left !important;
width: 16px !important;
height: 16px !important;
margin-right: 4px !important; }

.name > img[alt='Resident'] + *:after {
display: block !important;
float: right;
margin-top: -15px;
width: 16px;
height: 16px;
opacity: 0.3;
background: url('http://puu.sh/1bCAj') center center scroll no-repeat;
content: ''; }

.msg[onmouseover*="<img"] {
background: rgba(80,0,80,0.4) !important; }

}
