css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

body {
	background:#C0DEED url(https://twimg0-a.akamaihd.net/images/themes/theme1/bg.png) 0 0 fixed repeat-x !important;
}

#pageLogo a {
	opacity:.4;
}

#blueBar {
	background:#222 url(https://twimg0-a.akamaihd.net/a/1345858379/t1/img/twitter_web_sprite_bgs.png) 0 0 repeat-x;
	border-color:#222;
	-webkit-box-shadow: 0 2px 3px rgba(0,0,0,.25);
	-moz-box-shadow: 0 2px 3px rgba(0,0,0,.25);
	box-shadow: 0 2px 3px rgba(0,0,0,.25);
}

#globalContainer {
	padding:10px;
	background:url(https://twimg0-a.akamaihd.net/a/1345858379/t1/img/wash-white-30.png);
}

.hasLeftCol #mainContainer {
	border:0;
}

#contentCol {
	border-radius:5px;
	border:1px solid #ddd;
}

a.jewelButton:hover {
	background-color:rgba(255,255,255,.3) !important;
}


}
