css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xentax.com") {
.ltr, .quotecontent, .codecontent, .attachcontent, #logodesc, html, center, body, #content, .toc, #header, #page {
    background: #2C2C2C !important;
}
td, dl, #sidebar, .post {
    background: #333 !important;
    color: #eee !important;
}
.quotetitle, .codetitle, font, h1, h2, h3, h4, h5, li, b, p, th, .attachtitle {
    color: #eee !important;
}
.quotecontent {
    color: lightblue !important;
}
.codecontent {
    color: #ded !important;
}
.postauthor {
    color: #1B9E09 !important;
}
.forumrules, th, .quotetitle, .codetitle, #mw-head, #catlinks, .attachtitle {
    background: #111 !important;
}
.postdetails {
    color: snow !important;
}
.postlink, a, .gensmall {
    color: steelblue !important;
}
.username-coloured {
    color: #D92200 !important;
}
.copyright, .startcontent {
    display: none !important;
}
}
