css:
menubar > menu {
    margin-top: 3px !important;
    margin-bottom: 3px !important;
    padding-top: 3px !important;
    padding-bottom: 3px !important;
}

menubar > menu[open="true"] {
    color: #000000 !important;
}

.mail-toolbox, .toolbar-primary {
    -moz-appearance: toolbar !important;
}

.tabmail-tab:hover > .tab-stack > .tab-background:not([selected]) {
    background-image: none !important;
}

.tab-background[selected] {
    background-image: none !important;
}

.tabmail-tabs {
    -moz-appearance: tabpanel !important;
}

.tabmail-tab {
    -moz-appearance: tab !important;
    -moz-margin-end: -2px !important;
    margin-top: 1px !important;
    padding-left: 3px !important;
    padding-right: 3px !important;
    height: 28px !important;
}

.tabmail-tab:not([selected]) {
    -moz-appearance: tab !important;
    margin-top: 3px !important;
}

.tabmail-arrowscrollbox > scrollbox {
    -moz-padding-start: 0px !important;
}

.tabmail-tab:not([selected]):not(:hover) +
.tabmail-tab:not([selected]):not(:hover)::before {
    display: none !important;
}

.tab-text {
    color: -moz-DialogText !important;
    opacity: 1 !important;
}

.tab-content {
    -moz-padding-start: 0px !important;
    -moz-padding-end: 0px !important;
    margin-top: -3px !important;
}

.tree-stack {
    -moz-appearance: treeview !important;
}
