css:
@-moz-document url-prefix("http://reddit.com/r/breakingbad"), url-prefix("http://www.reddit.com/r/breakingbad") {
a[href="/s"]::after, a[href="#s"]::after {
    content: attr(title);
    background: transparent !important;
    color: black !important;
    }
a[href="/s"]:hover::after, a[href="#s"]:hover::after, a[href="/s"]:active::after, a[href="#s"]:active::after {
    color: black;
    background: transparent;
    }
}
