css:
#header .tab .tab_notice {
  color: white;

  font: normal 12.5px/1.4 "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif !important;

  font-weight: bold !important;

  background: #d93023 !important;

  -moz-border-radius: 3px !important;

  border-radius: 3px !important;

}

.tab_notice_nipple {

  background: url("http://i50.tinypic.com/2ppmyxv.png") no-repeat 

  scroll -400px -190px transparent !important;

  bottom: -8px;

  height: 7px;

  left: 6px;

  position: absolute;

  width: 8px;

}
