css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {
    div.l-header li#home_button > a { background: url("http://www.hapisan.com/tumblr/castle.png") no-repeat scroll 0px 0px transparent !important; width: 45px !important; height: 40px !important; position: relative !important; left: -8px !important; top: -12px !important; }
    div.l-header li#home_button > div.selection_nipple { left: -8px !important; }
    div.l-header li#home_button > span#new_post_notice_container { padding: 0 3px !important; border-radius: 0 !important; left: 22px !important; height: 13px !important; }
    div.l-header li#home_button > span#new_post_notice_container:after { border-radius: 0 !important; left: -1px !important; }
    div.l-header li#home_button > span#new_post_notice_container > span.tab_notice_value { font-family: Arial, sans-serif !important; font-size: 10px !important; }
    div.l-header li#home_button > span#new_post_notice_container > span.tab_notice_nipple { position: absolute !important; top: 0 !important; left: -1px !important; height: 17px !important; width: 1px !important; background: white !important; opacity: 0.5; }
    div.l-header li#home_button:hover > span#new_post_notice_container > span.tab_notice_nipple, div.l-header li#home_button:active > span#new_post_notice_container > span.tab_notice_nipple { opacity: 0.7 !important; }
    div.l-header li#home_button.selected > span#new_post_notice_container > span.tab_notice_nipple, div.l-header li#home_button.selected:hover > span#new_post_notice_container > span.tab_notice_nipple, div.l-header li#home_button.selected:active > span#new_post_notice_container > span.tab_notice_nipple { opacity: 1.0 !important; }
}
