css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.connectorride.com") {
/*header*/
body div div {
background-color: transparent !important; 
background: transparent none left; 
height: auto !important;
}
body div div a img {display: none;}
/*nav*/
body#trips div table tr td {background-color: #FFF;}
body div table ul#nav {background-color: #FFF; width: auto !important;}
body div table ul#nav li {background-color: #FFF;}
body div table ul#nav li a {background-color: #FFF;}
/*body*/
body div div#maincontent {background: #FFF none; font-family: 'Helvetica Neue', Arial, Tahoma, sans serif; height: auto; padding: 0px; margin-top: 8px;}
body div#topHdr img {display: none;}
body div div#maincontent table {background-color: #FFF; width: 998px !important;}
body div div#maincontent table div {padding: 5px; width: auto !important;}
body div div#maincontent table div.subhd {clear: left;}
body div div#maincontent table div p b {font-size: 125%;}
/*font corrections*/
#subLayout, #topHdr, h1, p, table, li, address  {font: 0.95em/1.3em "Helvetica Neue", Arial, Tahoma, sans serif;}
p {max-width: 55.0em;}
div.subhd {border-bottom: 0px;}
/*trips page*/
body#trips table.tabletrips {width: 100% !important; border-collapse: none; margin: 10px; padding: 0px;}
body#trips table.tabletrips td {border: 1px solid lime; text-align: left;}
body#trips table.tabletrips td hr {display: none; padding: 0px; margin: 0px;}
body#trips table.tabletrips td.texttableback, body#trips table.tabletrips td.texttableback {background-color: #E8E8E8; font-size:smaller;}
}
