css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



#inbox_button {

    padding-right: 17px !important;
}


#inbox_button a {

    height: 0 !important;
    width: 0 !important;
    top: -1px !important;
    padding-left: 30px !important;
    padding-top: 37px !important;
    background: url('http://media.tumblr.com/92c514dc3b3cafe8c4610cae332e4b93/tumblr_inline_miceouN1KK1qz4rgp.png') !important;
}


#inbox_button .tab_notice {
    left: 56px !important;
    right: auto !important;
}
#inbox_button.tab.iconic > .tab_anchor::before {
	content: none !important;
}
}
