css:
#inbox_button > a {
  background-image: none !important;
  position: relative !important;
  left: 3px !important;
  background: url("http://25.media.tumblr.com/tumblr_ma3gs8Hts11r7wvzxo1_r1_100.png") !important;
  background-size: 40px 45px !important;
  background-repeat: no-repeat !important;
  opacity: .65 !important;
  -webkit-transition: ease-in all .25s !important;
  -moz-transition: ease-in all .25s !important;
}
#inbox_button > a:hover {
  opacity: 1 !important;
  -webkit-transition: ease-out all .25s !important;
  -moz-transition: ease-out all .25s !important;
}
