css:
@-moz-document domain("www.readwriteweb.com") {
  body {
    margin: 0 !important;
    padding: 0 !important;
  }

  body > p {
    display: none !important;
  }

  #header,
  #content-column-right,
  #trackbacks,
  #comments-open,
  #footer,
  .leaderboard,
  .asset-footer {
    display: none !important;
  }

  #wrapper {
    width: 100% !important;
    margin: 0 !important;
    padding: 0 !important;
    background: none !important;
    line-height: 1.3em !important;
  }

  #metadata_digg_box {
    width: 100% !important;
  }

  #metadata_digg_left {
    width: 80% !important;
  }

  #content-column-left {
    float: none !important;
    width: 100% !important;
  }

  #left_column_style {
    padding: 0 !important;
  }

  .asset-content > a {
    display: none !important;
  }

  #comments,
  .asset-body,
  .asset-more {
    font-size: 13px !important;
    line-height: 1.3em !important;
  }

  #comments > p {
    display: none !important;
  }

  #comments > ul {
    margin-bottom: 0 !important;
  }

  #comments > ul > br {
    display: none !important;
  }

  .asset, .comments, .trackbacks, .archive {
    overflow: visible !important;
  }
}
