css:
@-moz-document domain(tumblr.com) {
#home_button > a {
  background-image: none !important;
  position: relative !important;
  left: 3px !important;
  background: url("http://img259.imageshack.us/img259/343/thurmanicon01.png") !important;
  background-size: 30px 40px !important;
  background-repeat: no-repeat !important;
  opacity: .85 !important;
  -webkit-transition: ease-in all .25s !important;
  -moz-transition: ease-in all .25s !important;
}
#home_button > a:hover {
  opacity: 1 !important;
  -webkit-transition: ease-out all .25s !important;
  -moz-transition: ease-out all .25s !important;
}
}
