css:
@-moz-document url-prefix("https://github.com/organizations/paperboy-goope"), url-prefix("https://github.com/paperboy-goope") {.github-logo-4x-hover {
        opacity:0;
}

.header {
        background: #f6f3f2; /* Old browsers */
        background: -moz-linear-gradient(top,  #f6f3f2 1%, #e4e4d2 100%); /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#f6f3f2), color-stop(100%,#e4e4d2)); /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* Opera 11.10+ */
        background: -ms-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* IE10+ */
}

.header a {
        text-shadow: none;
        color: #313131;
}

.pagehead .container > ul.tabs {
        background: #f6f3f2; /* Old browsers */
        background: -moz-linear-gradient(top,  #f6f3f2 1%, #e4e4d2 100%); /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#f6f3f2), color-stop(100%,#e4e4d2)); /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* Opera 11.10+ */
        background: -ms-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 100%); /* IE10+ */
}

.pagehead .container > ul.tabs li a {
        text-shadow: none;
        color: #999;
        border-bottom: 2px solid #fff779;
}

/* hover */
.pagehead .container > ul.tabs li a:hover{
        background: #e4e4d2; /* Old browsers */
        background: -moz-linear-gradient(top,  #e4e4d2 1%, #f6f3f2 99%); /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#e4e4d2), color-stop(99%,#f6f3f2)); /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top,  #e4e4d2 1%,#f6f3f2 99%); /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top,  #e4e4d2 1%,#f6f3f2 99%); /* Opera 11.10+ */
        background: -ms-linear-gradient(top,  #e4e4d2 1%,#f6f3f2 99%); /* IE10+ */
        border-bottom: 2px solid #fb968a;
        color: #323232;
}

.pagehead .container > ul.tabs li a.selected, 
.pagehead .container > ul.tabs li a.selected:hover {
        background: #f6f3f2; /* Old browsers */
        background: -moz-linear-gradient(top,  #f6f3f2 1%, #e4e4d2 99%); /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#f6f3f2), color-stop(99%,#e4e4d2)); /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 99%); /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 99%); /* Opera 11.10+ */
        background: -ms-linear-gradient(top,  #f6f3f2 1%,#e4e4d2 99%); /* IE10+ */

}

.pagehead .container > ul.tabs li a.selected, 
.pagehead .container > ul.tabs li a.selected:hover {
        color:#043040;
        border-bottom: 2px solid #fb968a;
}

.markdown-body h1 {
  background-color: #313131;
  padding-left: 1px;
  border: 2px solid #E4E3D2 !important;
  border-radius: 3px;
}
}
