css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com")

{

.l-header-container--refresh .tab_bar .tab_home .tab_anchor:before 
{
content: none !important;
}

#home_button
{
padding-right: 10px !important;
}

#home_button a
{
height: 0 !important;
width: 50px !important;
top: -5px !important;
padding-left: 25px !important;
padding-top: 30px !important;
margin-top: 5px !important;
left: 25px !important;
background: url('http://img.photobucket.com/albums/v420/KaKaVegeGurl/SG1Button22.png') no-repeat !important;
}

#home_button .tab_notice
{
left: 26px !important;
right: auto !important;
}
  
.l-header .selection_nipple
{
top: 8px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/inbox")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/help")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com/settings")

{
  
.l-header .selection_nipple
{
top: 5px !important;
}
}
