css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.nicovideo.jp/my)  {

#mylist .articleBody .myContList li.SYS_box_item .thumbContainer img,#mylist .articleBody .myContList li.SYS_box_item .thumbContainer a:not(.mylist_award){
height: 100px!important;
width: 130px!important;
}
#mylist .articleBody .myContList li.SYS_box_item .thumbContainer a.mylist_award img{
height: 14px!important;
width: 13px!important;
}

#mylist .articleBody .myContList li.SYS_box_item{
width: 212px!important;
min-height: 340px!important;
float:left!important;
padding:10px!important;
}
#mylist .articleBody .myContList li.SYS_box_item:nth-child(2n){
background:#DCE8FA;
}
#mylist .articleBody .myContList li.SYS_box_item .mylistVideo,#mylist .articleBody .myContList li.SYS_box_item .mylistVideo h5{
width: 212px!important;
}
#mylist .articleBody .myContList li.SYS_box_item .commentContainer p{
width: 190px!important;
}
.pagerWrap{
float:right!important;
}
#mylist .articleBody .myContList li.SYS_box_item ul.metadata li{
display:block!important;
line-height:1.3em!important;
}
#mylist .articleBody .myContList li.SYS_box_item ul.metadata{
background:transparent !important;
display:block!important;
padding-left:0px!important;
}
.articleBody{
margin:0px!important;
width:700px!important;
}
#mylist .articleBody .myContList li.SYS_box_item .thumbContainer a.mylist_award{
width:130px!important;
}
#mylist .articleBody .myContList li.SYS_box_item .mylistVideo h5{
font-size:13px!important;
}


}
