css:
/* =======================================================================
    updates: http://userstyles.org/styles/74831/deviantart-com-hide-all-ads
more styles: http://userstyles.org/users/125600
======================================================================= */

@-moz-document domain("deviantart.com") {

    /* hide all ads */
    .textbanner-ad, /* deviant - top ad */
    .partial-ad, /* deviant - right ad */
    div#gmi-GPage > div:nth-of-type(2)[style="text-align:center"], /* profile - top ad */
    #gmi-GZone[gmi-name="ad_zone"], /* profile - adcast */
    .sleekadbubble, /* browsing - ad */
    [class^="blt-ad-"], /* browsing - ad */
    .gruze-sidebar .guideblock iframe, /* groups - sidebar ad */
    [class*="you-know-what"], /* some other ads */
    [id*="you-know-what"], /* some other ads */
    [class*="dp-ad-chrome"], /* some other ads */
    [class*="dp-ad-visible"], /* some other ads */
    [class*="-ad-box"], /* large ad on front page under menu */
    .f.gruze-sidebar.right-sidebar.gruze-sidebar-end.module-min.module-blocks, /* right side ad in group gallery*/

    x{
        visibility: hidden !important;
        display: none !important;
    }
    
    /* fixed some positioning */
    .messages-right .mczone{
        position:relative;
        top:-1px;
    }
    .dev-view-deviation{
        margin-top:0 !important;
    }
    table.messages tbody tr td.f.messages-left+td.f{
        overflow:hidden;
    }
    
    /* make links to graphicriver.net stand out */
    a[href*="graphicriver"]{
        background-color:#aeb8ac;
    }
    
    /* about updates */
    .browse-top-bar{
        background-image:url("http://graenfur.heliohost.org/deviantart/da_update_2014_05_31.png");
        background-repeat:no-repeat;
        background-position:0 0;
    }
    .browse-top-bar:hover{
        background-position:0 -50px;
    }
}
