css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
#gmi-CCommentThread .ccomment{margin-bottom:4px !important;}
#gmi-CCommentThread .cc-meta{margin-bottom:0px !important;}
#gmi-CCommentThread .ccomment .cc-in{
    padding:0px 4px 4px 4px !important;
    /*Comments font size*/
    font-size:10px;
}
#gmi-CCommentThread .cc-name{font-size:11pt !important;}
#gmi-CCommentThread .cc-userdetails,.cc-time{font-size:9px !important;}
#gmi-CCommentThread .cc-avatar{margin:2px 14px 0px 2px !important;}
#gmi-CCommentThread .ccomment i.l{left:56px !important;}
#gmi-CCommentThread .ccomment a.reply{margin-bottom:2px !important;left:1px !important;width:42px !important;top:54px !important;}
#gmi-CCommentThread .creason{font-size:9px;padding:0 !important;}
#gmi-CCommentThread span.cc-mood{padding-top:4px;}
#gmi-CCommentThread .cc-signature{font-size:7pt !important;}
}
