/* http://www.reddit.com/r/Enhancement/comments/k7avm/feature_request_ability_to_minimize_my_subreddits/ */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.reddit.com"), domain("reddit.com") 
{
    /* let's hide the logout link, pref-lang, and the userbar toggle */
    a[onclick='$(this).parent().submit()'], .pref-lang, #userbarToggle, #DashboardLink {
        display: none;
    }
    #header-bottom-right .separator {
        font-size: 3px;
        color: #EFF7FF !important;
    }
    #RESSettingsButton {
        margin-top: 2px !important;
        margin-bottom: -4px !important;
    }
    .userkarma { border-bottom: none !important; }
}
