css:
/* clean up Twitter! */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.twitter.com"), domain("twitter.com") {
    /* hide stupid trends box */
    .trends, .js-wtf-module, .site-footer {
        display: none !important;
    }
    .last-new-tweet {
        border-bottom: 5px dotted lightgrey !important;
    }
    .my-tweet, .js-retweet-text {
        background-color: lightcyan;
    }
    #page-container {
        width: 1200px !important;
    }
    .content-main {
        width: 1200px !important; /* was 890px */
    }
    .dashboard {
        width: 1200px !important;
    }
    .js-mini-profile-stats-container {
        margin-top: -50px;
        margin-left: 200px;
    }
    .js-mini-profile-stats {
        border: none !important;
    }
    .js-mini-profile-stats-container a {
        border: none !important;
    }
    .module .tweet-user {
        margin-top: -50px;
        margin-left: 900px;
        background-color: #f9f9f9 !important;
    }
    .module {
        background-color: #f9f9f9 !important;
    }
    .flex-module {
        border-radius: 5px !important;
    }
    .js-tweet-text, .stream-item-header {
        font-size: 16px;
    }
}
