css:
@-moz-document domain("tumblr.com") {
/*TURN OFF ORIGINAL ICON*/
#posts_control .posts,
#dashboard_controls_open_blog .followers,
#dashboard_controls_open_blog .drafts,
#dashboard_controls_open_blog .queue {
  background-image: none !important;
}

/*SET POSITION, SIZE & UNIVERSAL*/
#posts_control > .posts:before,
#dashboard_controls_open_blog .followers:before,
#dashboard_controls_open_blog .drafts:before,
#dashboard_controls_open_blog .queue:before {
  position: absolute !important;
  content: "" !important;
  top: 2px !important;
  left: 4px !important;
  width: 34px !important;
  height: 34px !important;
  background-size: 34px 34px !important;
  border-radius: 4px !important;
}

/*INSERT REPLACEMENT ICONS*/
#posts_control > .posts:before {
  background-image: url("http://www.khwiki.net/images/e/e0/Savage_Fantasy_KH3D.png") !important;
}
#dashboard_controls_open_blog .followers:before {
  background-image: url("http://www.khwiki.net/images/d/da/Lofty_Fancy_KH3D.png") !important;
}
#dashboard_controls_open_blog .drafts:before {
  background-image: url("http://www.khwiki.net/images/7/7a/Dulcet_Fantasy_KH3D.png") !important;
}
#dashboard_controls_open_blog .queue:before {
  background-image: url("http://www.khwiki.net/images/2/2f/Prickly_Fantasy_KH3D.png") !important;
}
}
