css:
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
body, html {background: #222222!important;}
.selection_nipple, .nipple {display: none!important;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: #116181!important;}
.controls_section {background: #222222!important;}
#search_form #search_query {background: #222222!important;}
#search_controls_button.popover_button {width: 10px!important;}
#search_controls.popover {background: #ffffff!important;}
#search_controls.popover a:hover {background: #888888; color: #ffffff!important; text-shadow: none!important;}
.radar .content.is_media {background: none!important;}
.radar .mask.top {background: none!important;}
.radar .mask.bottom {background: none!important;}
.radar .content.is_media .photo {border-radius: 5px;}
.radar #radar_attribution.attribution {background: #222222; padding-top: 5px; padding-left: 5px; padding-bottom: 3px; margin-right: 10px; margin-top: 0px; border-bottom-left-radius: 8px; border-bottom-right-radius: 8px;}
.footer_links {white-space: normal!important; height: auto; word-wrap: break-word;}
.footer_links.with_tags {white-space: normal!important; word-wrap: break-word;}
.source_url, .tags {display: inline !important; white-space: normal !important;}
.source_url_gradient {display: none !important;}
#posts .post .permalink {background: url('http://img716.imageshack.us/img716/7757/tabbu.png');}
#right_column::after {background: transparent;}
.post_text_wrapper p a {background: rgba(0,0,0,0.1)!important;}
#pagination a {color: rgba(255, 255, 255, .69)!important; font-size: 14px!important; padding: 8px 13px 10px 13px!important; -moz-border-radius: 5px!important; -webkit-border-radius: 5px!important; border-radius: 5px!important; border: 1px solid rgba(0, 0, 0, .11)!important; background-color: #323232!important;}

#dashboard_following #content_bottom {display: none!important;}
#dashboard_following #left_column {margin-bottom: 20px!important;}
#dashboard_following #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {border-bottom-left-radius: 10px!important; border-bottom-right-radius: 10px!important;}
#main_help div.l-content, #main_custom_themes div.l-content, #dashboard_ignore div.l-content, #main_doc_page div.l-content, #dashboard_directory div.l-content, #dashboard_lookup_contacts div.l-content, #dashboard_following #left_column {border-radius: 5px!important; background: #fff!important;}
#dashboard_following .grey_note {background: #ffffff!important;}
}
