@-moz-document domain("crashplan.com"), domain("code42.com") {

/* Tweets - Left Sidebar */
#social-widget, #tweet, #twitter-tip { display: none !important }

/* Downloads - Left Sidebar */
#downloads { display: none !important }

/* "Get our FREE app" */
#mobile-ad { display: none !important }

/* Footer */
#footer { display: none !important }

}
