css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nyaa.eu") {
#main{
width:100% !important;
}
.content{
width:100% !important;
border:0px !important;
}
.content form, .tlistsortorder{
width:auto !important;
text-align:center !important;
margin:0 auto !important;
}
.tlistsortorder{
margin-top:-50px !important;
}
[class="notice"], #chatbox{
display:none !important;
}
.tinfodownloadtxtbutton{
width:auto !important;
}
[class="tlisticon"]{
width:85px !important;
display:table-cell !important;

}
[class="tlistname"]{
margin-left:0px !important;
}
div [style="float:right;width:11.9%;"], img[alt=""], .torrentcats, .tlistsortorder, .refinesearch{
display:none !important;
}
}
