css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mangafox.me") 
{
img{
width:auto!important;
}
#spotlight, #left-skyscraper,#right-skyscraper {
display:none!important;
}

#intro {display: none !important}

#footer {display: none !important}

iframe {display: none !important}

#ad_top {display: none !important}

#bottom_ads {display: none !important}

}

@-moz-document url-prefix("http://mangafox.me/manga") {

.tips {display: none !important}

.chlist .tips, .tplist .tips {display: inline !important }

#shares {display: none !important}

#footer {display: none !important}

#tool {display: none !important}

#viewer {padding-top: 10px !important;}



#top_left_bar,

#top_center_bar,

#top_right_bar {height: 50px !important;}



#top_left_bar {width: 7px !important; background: url(http://l.mfcdn.net/media/search.bottom.left.png) no-repeat !important;}

#top_center_bar {background: url(http://l.mfcdn.net/media/search.bottom.center.png) repeat-x !important;}

#top_right_bar {width: 7px !important; background: url(http://l.mfcdn.net/media/search.bottom.right.png) no-repeat !important;}



#bottom_left_bar,

#bottom_center_bar,

#bottom_right_bar {display: none !important}

.ad {display: none !important}

}

@-moz-document domain("mangahere.com") 
{

}

@-moz-document url-prefix("http://www.mangahere.com/manga") {
a img{
width:auto!important;
}

}
