@-moz-document domain("streamcloud.eu") {
#header, #banner {
display:none !important;
}

div#page .header {
background: none;
}

div#page .header ul {
display: none;
}

div#page .content .padding {
padding: 0px 45px;
}

div.header.page {
height: auto !important;
}

div#page .header h1 {
margin: 10px auto 2px auto;
padding: 10px 0px 10px 0px;
border-bottom: 3px solid #00ddff;
width: 890px !important;
}

.addthis_toolbox {
display: none;
}

div#page .bottom {
display: none;
}

#footer {
display: none;
}

#page {
background:none;
}

#turnon, #turnoff {
display: none !important;
}

.content {
background:none !Important;
}

body {
