css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://browser/content/browser.xul"){

#navigator-toolbox[mode="icons"][iconsize="large"] #nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #back-button{
  -moz-image-region: rect(21px, 16px, 35px, 2px) !important;
  mask:none !important;
}
#navigator-toolbox[mode="icons"][iconsize="large"] #nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #forward-button{
  -moz-image-region: rect(21px, 16px, 35px, 2px) !important;
  mask:none !important;
  -moz-transform: scaleX(-1) !important;
  transform: scaleX(-1) !important;
}

#navigator-toolbox[mode="icons"][iconsize="large"] #nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #back-button .toolbarbutton-icon,
#navigator-toolbox[mode="icons"][iconsize="large"] #nav-bar:not([currentset*="unified-back-forward-button,urlbar-container"]) #forward-button .toolbarbutton-icon{
  padding:6px 6px !important;
  margin:-6px 0 !important;
  background-clip:padding-box !important;
}

}
