css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(chrome://navigator/content/navigator.xul){

#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]),
#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]) #navigator-toolbox,
#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]) #status-bar,
#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]) .toolbox-top > *,
#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]) #sidebar-box,
#main-window[windowtype="navigator:browser"]:not([stylish-url$=pdf]) #linktoolbar > *{
  -moz-appearance:-moz-win-glass !important;
  background-color:transparent !important;
  text-shadow: white -2px -2px 4px, white 2px 2px 4px/*, white 2px -2px 4px, white -2px 2px 4px */!important;
}

#main-window[windowtype="navigator:browser"] #menubar-items{
  background-color: rgba(255, 255, 255, 0.5) !important;
  border-radius: 4px 4px 4px 4px !important;
  transition: background-color ease-out 1s;
  text-shadow: white -2px -2px 4px !important;
}

#main-window[windowtype="navigator:browser"] #menubar-items:-moz-window-inactive{
  background-color:transparent !important;
}

.toolbar-grippy,
toolbargrippy{
  opacity:0.6 !important;
  -moz-transition: opacity ease-in 0.8s;
}

.toolbar-grippy:hover,
toolbargrippy:hover{
  opacity:1 !important;
}

.toolbar-holder{
  border:none !important;
}

.collapsed-tray-spacer{
  display:none !important;
}

#sidebar-splitter{
  background-color: transparent !important;
}

#FindToolbar{
  border:none !important;
}

/*optional*/
