css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://news.ycombinator.com") {

body {
    font-size:150%
}

.title {
    font-family: Georgia !important;
    font-size:20px !important;
    color:#f6f6ef !important;
}

.title a {
    color: #000 !important;
}

.title a:visited {
    color: #777 !important;
    font-style: italic !important;

}

.comhead
{
    font-family: Georgia !important;
    font-size:16px !important;
    color: #555 !important;
}

.subtext {
    padding-left:30px !important;
}

.subtext,
.subtext span,
.subtext a {
    font-family: Georgia !important;
    font-size:14px !important;
    color: #fff !important;
}

.subtext span,
.subtext a {
    color: #bbb !important;
    display:block; float: left;
    width:150px;
}

.subtext a:hover {
    color: #000 !important;
}

.subtext a font {
    color: #bbb !important;
}

.comment
{
    font-family: Georgia !important;
    font-size:18px !important;
    color: #555 !important;
    display:block;
    max-width: 800px;
    line-height:150%;
}

td.default u a {
    float:right;
}

body {
    background-color: #222;
}

body > center > table {
    background-color:white !important;
    padding:30px;
    box-shadow: 0 0 30px #000,0 0 20px #000000 inset ;
}

html > body > center > table > tbody > tr > td {
    background-color: #fff !important;
}


}
