css:
@-moz-document domain(youtube.com){
  div.guide-section:nth-child(2) > :nth-child(n+2), div.guide-section:nth-child(4) > :nth-child(n+2), .guide-reminder.upgrade, #video-sidebar {
    display:none;
  }
  #feed, #feed-background {
    margin-right:0 !important;
    width:770px !important;
  }
  .feed-item-dismiss-menu {
    right:75px !important;
  }
  .feed-author-bubble {
    float:right !important;
    background-image:none !important;
    margin-right:0 !important;
    width:28px !important;
    height:28px !important;
  }
  .feed-item-author {
    margin:0 !important;
  }
  .feed-item-action-menu {
    right:54px !important;
  }
  .feed-item-header {
    margin-left:128px !important;
    margin-bottom:10px !important;
  }
  .feed-item-thumb {
    margin-top:-22px !important;
  }
  .feed-item-thumb .video-thumb {
    width:185px !important;
    height:104px !important;
  }
  .feed-item-thumb .video-thumb img {
    width:128px !important;
  }
  .feed-item-content > h4 {
    display:inline !important;
    line-height:normal;
  }
  .feed-item-content > h4 > a, .feed-item-content > .metadata {
    display:inline !important;
  }
  .feed-item-content > .metadata > .view-count {
    vertical-align:baseline !important;
  }
  .feed-item-content > .metadata > .description {
    margin-top:4px !important;
  }
}
