css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("motls.blogspot.com")
{
/*frames & containers to hide*/
 div[id='navbar'] {
    display: none !important;
  }

 iframe[id="google_ads_frame1"] {
    display: none !important;
  }
 
 div[id='navbar'] {
    display: none !important;
  }


 #aswift_1_anchor {
    display: none !important;
  }

 
 div[id='HTML3'] {
    display: none !important;
  }


/*text&background*/
html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: black !important;
background: white url(http://www.google.com/mapfiles/arrowtransparent.png) !important; 
}
select, button
{
background-color: white !important;
}
div, span, a
{
background-color: white !important;
color: black
}
legend, select, input, span, code, textarea, pre, label, div, dd
{
color: black !important;
background-color: white !important;
}


div[id=ads]{display: none !important;}


/*links*/
a
{
background-color: white !important;
color: #004000 !important;
}
a:visited
{
background-color: white !important;
color: #004300 !important;
}

/*highlight*/
textarea, pre, input, code
{
background-color: white !important;
color: #ffffff !important;
}

}
