/* ==UserStyle==
// @name         Len R's streamlined hulu
// @author       LenR
// @version      v1.1
//
// ==/UserStyle==*/


/***********************************************
                  video page
 ***********************************************/
@-moz-document url-prefix("http://www.hulu.com/watch/") {
  div.gr.b.ptop {
	margin-top: -16px !important; }

  div.gr.b.ptop >div.description {
	margin-top: -7px !important; }

  DIV[style="padding-bottom: 10px;"][class="description"] {
	width: 100em !important; }

  #description-content >.standard.tidy >.layout.b-c {
	/*display: none !important;*/
	margin-bottom: -18px !important; }

  embed[id="player"] {
	margin-top: -72px !important;
	margin-left: -130px !important;}

  .tune-in-text {
	margin-top: -402px !important;
	margin-left: 320px !important; }

  .copyright-text { display: none; }

  #episode-container {
	margin-top: -90px !important;
	margin-bottom: -20px !important;}

  #episode-container >div >div, #clip-container >div >div {
	margin-top: -8px !important; 
	margin-bottom: -8px !important; } /*tight containers*/

  DIV[style="border-top: 1px solid rgb(238, 238, 238); padding-top: 15px;"][class="fixed-lg container"] {
	margin-top: -32px !important; } /*episode lists div*/

  .fixed-lg.container >table {
	margin-top: -14px !important; 
	margin-bottom: -18px !important; } /*ep lists tight*/
} /*@-moz-document url-prefix("http://www.hulu.com/watch/")*/


/***********************************************
                   search page
 ***********************************************/
@-moz-document url-prefix("http://www.hulu.com/videos/search?") {
  div.section >div.gr.b { /*top section*/
	margin-top: -10px !important;
	margin-bottom: -24px !important; }

  .fixed-lg.relative.container { /*move results up*/
	margin-top: -36px !important;}

  div.gr.a.leftnav > div:first-child { /*side alignment*/
	margin-top: 3px !important;
	margin-left: 3px !important;
	margin-bottom: -3px !important; }

  #results_list {
	margin-top: 3px !important;} /*table dn for outline*/

  #results_list > TBODY > .thumbs > TD > H3,
  #results_list > TBODY > .thumbs > TD > DIV{
	margin-top: -12px !important; 
	margin-bottom: -16px !important; }

  /*span.playlist-link[id^="add_video"] {} /*lost +queue*/

  div.gr.a.leftnav > div:first-child:hover,
  #results_list > TBODY > .thumbs > TD:hover {
	font-weight: bold !important;
   	outline: 3px solid !important;
   	outline-color: rgba(0,0,166,.6) !important;
   	-moz-outline-radius: 6px !important;}

} /*http://www.hulu.com/videos/search?*/
