css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hopeinenomena.net") {
* { font-family: helvetica !important; }
a:link, a:visited { color: #c00 !important; }
h3 a:link, .postprofile a:link { color: #666 !important;} 
h3 { letter-spacing: -0.02em; font-size: 22px !important; }
#wrap { width: 800px !important; margin-left: auto !important; margin-right:auto !important; }
.post, .display-options { border-top: 28px dotted #f0f0f0 !important; margin-top: 20px !important; padding-top: 20px !important; background: transparent !important}
blockquote { background: transparent !important; border: 1px dotted #ccc !important; color: #999 !important; font-size: 10px !important;}
.postprofile, .signature, .author, .author a { color: #999 !important; font-weight: normal !important; }
.postprofile img { display: none !important; }
.googleAd { display: none !important;}
#sidebar { display: none !important;}
#contentArea { width: 100%; float:right;}
}
