css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://userstyles.org/") , url-prefix("https://userstyles.org/") {

body {
 background: #000!important;
}

#left-sidebar nav, #left-sidebar > *, h1, #main-header {
 background: #111!important;
}

a, span, p, td, #long-description, #switch-browser-note {
 color: #fff!important;
 text-decoration: none!important;
}

a, th {
 color: #7ff!important;
}

h1, h2, h3, a:hover, #show-search-options:hover {
 color: #ff0!important;
}


.ad {
 display: none!important;
}
















}
