css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.livedoor.com") {
html,
body,
#commonHeader
{
    background:none !important;
    background-color:#efefef !important;
}

#container-wrapper,
#newsContents
{
    width:800px !important;
    margin:auto !important;
}

.column-inner
{
    margin:0 !important;
    background-color:#efefef !important
}

#content
{
    margin:0 !important;
}

.article
{
    font-size:12pt !important;
}

a:link {
    color: #0000ee !important;
}

a:visited {
    color: #551a8b !important;
}
a:hover {
    color: #ee0000 !important;
}

#navigation,#footer,#aside,
#functionHeader, #newsHeader, #tagline,#subColumn,
#newsFooter,#ldFooter,#commonFooter,#social-count-box,
#article-social-tool,#article-social-comment,
.tool-list
{
    display:none;
}
}
