css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("alc.co.jp") {
div#AreaBody
{
    width:800px !important;
}

div#AreaUpperLeft,
div#AreaUpperLeftContainer
{
    width:800px !important;
    max-width:800px !important;
    margin:0 !important;
    font-size:12pt !important;
    line-height:18pt !important;
}

#body-tab0,
.inner,
.inner2
{
    background:none !important;
    padding:0 10px !important;
    text-align:center !important;
}

div#search_area
{
    width:800px !important;
    padding:40px 0 !important;
    background-color:#eee !important;
}

#contents_inner
{
    margin:0 !important;
}

input#q,
form#fm1 > input.s_txt
{
    width:400px !important;
    height:40px !important;
    font-size: 20pt !important;
}

#wrapper
{
    width:800px !important;
}

#tabhead,
#header,#gnav,#sub_right,#sub_left,#main,#footer,#pagetop,#copyright,
#AreaHeaderLeft,#AreaHeaderRight,#AreaSponsorSite,#AreaFooter,
#AreaLowerLeft,
.cl_l,.bg_i,.smallredfont,
nobr,
.link,
.pro_btn,
form#fm1 > a
{
    display:none !important;
}
}
