/* ==UserStyle==
// @name	LR FFX3 bookmark bar hover compression
// @author	LRodman
// @version	1.2
// @author	LRodman

// ==/UserStyle==*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*all transparent*/
#bookmarksBarContent toolbarbutton:not(:hover):not([container="true"]) {
	opacity: .8 !important; }

/*folders*/
.bookmark-item[container="true"] {
	margin-right: -1px !important; } 
.bookmark-item[container="true"]:not(:hover) {
	max-width: 60px !important; /*}
.bookmark-item[container="true"]:not(:hover) image {*/
	opacity: .7 !important; }
.bookmark-item[container="true"] image {
	margin-left: -1px !important;
	margin-right: 0px !important; }	
	
.bookmark-item:not([container]):hover image {
	margin-left: 0px !important;
	margin-right: 2px !important; } 

/*bookmark labels gone*/
toolbarbutton:not([container]):not(:hover) label {
	display: none !important;}

toolbarbutton/*:hover*/ .toolbarbutton-text {
	font-weight: bold !important;  }


