css:
@-moz-document url-prefix("http://digital.lib.lehigh.edu/trial/pocahontas/images.php") {
h1, .nav, .footer {display: none;}

.content {
        margin: 0;
	padding: 1em;
	width: 100%;
	box-sizing: border-box;
	text-align: center;
}body {
	background: #444;
}

img{
	margin: 3px;
	box-shadow: 0 0 5px 5px #333;
	border-radius: 2px;
}
.content p {
  font-family: Avenir Next;
  text-align: left;
  border-radius: 25px;
  background: rgba(200,200,200,.75);
  padding: 10px;
  margin: 1em;
}
.content p:first-child {
  text-align: center;
  opacity: .5;
}
}
