    border-radius: 5px 5px 5px 5px!important;}


.site, .site-img {
    border-radius: 8px!important;}

.site-strip {
    border-radius: 6px 6px 0px 0px!important;}

.site-title {
    border-radius: 0px 0px 6px 6px!important;}

#newtab-grid {
    border-radius: 5px 5px 5px 5px!important;}

.newtab-site, .newtab-thumbnail, .newtab-cell {
    border-radius: 8px!important;}

.newtab-title {
    border-radius: 0px 0px 6px 6px!important;}
}
