css:
@-moz-document regexp("^(http:\\/\\/www.)*quizlet.nl((?!go).)*$") {
#ad_leaderboard {
visibility:hidden!important;
}

#ad_leaderboard:after {
color:#FFF;
content:'Template door DarkSavior'!important;
visibility:visible;
}

#contact-details a {
color:#FFF!important;
font-size:12px!important;
}

#contact-details a:hover {
color:#7A7A7A!important;
}

#filters a:hover,.selected,#portfolio-navi a:hover {
background-color:#404040!important;
border:1px solid #404040!important;
}

#forum .forum_links a,#forum .forum_rechts a {
font-weight:700!important;
}

#forum .forum_links a:hover,#forum .forum_rechts a:hover,#forum .forum_post a:hover,.large-notice a:hover,div.blok div ul li a:hover {
text-decoration:underline!important;
}

#forum .forum_links a:not([title*="moderator"]),#forum .forum_rechts a:not([title*="moderator"]) {
color:#36F!important;
}

#forum .forum_post .forum_rechts {
font-size:15px!important;
}

#forum .forum_post .forum_rechts .post_info,#forum .forum_post .forum_rechts div.signature,form.default p,.large-notice h1,input[type="text"],input[type="password"],input[type="email"],textarea,select,div#poll div form p b,.bbcode_list li,.cd-timeline-block,.cd-timeline-content p {
color:#000!important;
}

#important_links {
border-color:#404040!important;
}

#navigation ul li a:hover,#navigation ul li:hover > a,#bolded-line,.button.gray:hover,.button.light:hover,.button.color,input[type="button"],.flex-direction-nav .flex-prev:hover,.flex-direction-nav .flex-next:hover,.tooltip-inner,#scroll-top-top a,.post-icon,.avatar:hover,.pagination li:hover {
background-color:#404040!important;
}

#navigation ul ul li.third a:hover {
background:#7A7A7A!important;
}

#page-title h4,a,#fade li,li,p,p strong,p a,p a:visited,.quizlet_list a,.large-notice p,div.links,div.blok div ul li a:visited,.acc-trigger a:hover,.acc-trigger.active a,#chapter_inhoud,.search-form .search-text-box {
color:#FFF!important;
}

#ubb_row {
background-color:#FFF!important;
}

#wrapper,.acc-trigger {
background:#000!important;
}

.bbcode_list {
margin-left:25px;
margin-top:10px;
}

.button.light {
background:#404040!important;
color:#FFF!important;
}

.cd-timeline-content h2 {
color:#303e49!important;
}

.cluetip-rounded .cluetip-outer {
background-color:#323232!important;
}

.columns img,.column img {
max-width:80%!important;
}

.forum_links {
overflow:hidden!important;
}

.large-notice {
background-color:#343434!important;
}

.large-notice a {
color:#00A8CA!important;
}

.mini-ico-flag[style*="float:right"] {
background-image:url(http://www.readsoft.com/Content/Images/exclamation_red.png)!important;
background-position:0 0!important;
float:none!important;
margin-left:2px;
margin-right:2px;
}

.mr-rotato-prev:hover,.mr-rotato-next:hover,li.current,.tags a:hover {
background-color:#404040!important;
border-color:#404040!important;
}

.notification.error {
border:1px solid #000!important;
}

.notification.success {
background-color:#7A7A7A!important;
border:1px solid #000!important;
color:#FFF!important;
}

.notification.success .close {
background:url(http://i.imgur.com/sUlZLpK.png) 0 -5px no-repeat!important;
}

.pagination li {
background:#404040!important;
}

.quizlet_list p {
color:#858585!important;
}

.rechts a {
color:aqua!important;
}

.shadetabs li a {
background:gray!important;
}

.tooltip.top .tooltip-arrow {
border-top:5px solid #404040!important;
}

a:hover,a:focus,#online_crew select {
color:#404040!important;
}

blockquote ul > li {
margin-left:20px;
}

body {
background:#0F0F0F!important;
color:#FFF!important;
}

div.bbcode_code_body {
background-color:gray!important;
color:#FFF!important;
}

form.default a,#forum .forum_post .forum_rechts p.edited {
color:gray!important;
}

form.default input.button,.headline h3,.headline h4 {
background-color:#404040!important;
color:#FFF!important;
}

form.default label {
color:#000!important;
font-size:13px!important;
}

form.default textarea {
color:#000!important;
font-size:15px!important;
}

h1,h2 {
color:#E0E0E0!important;
}

img[src*="http://www.quizlet.nl/images/folder_open.gif"] {
content:url(http://i.imgur.com/7LGIhH9.png)!important;
}

img[src*="http://www.quizlet.nl/images/forum_topic_2.gif"] {
content:url(http://i.imgur.com/cPiFuPv.png)!important;
height:auto!important;
width:auto!important;
}

img[src*="http://www.quizlet.nl/images/forum_topic_3.gif"] {
content:url(http://i.imgur.com/fysNsmL.png)!important;
}

table.standard-table a {
color:#FFF;
font-size:13px!important;
font-weight:700!important;
}

table.standard-table a:visited {
color:gray!important;
font-size:13px!important;
font-weight:700!important;
}

table.standard-table th {
background-color:#404040!important;
color:#FFF;
}

table.standard-table tr:hover td,table.standard-table td,.quizlet_list,div.minimal,#portfolio-navi a,div.bbcode_code_head {
background-color:#000!important;
}
}
