css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {
.count,
.likes .hide_overflow,
.tag .hide_overflow,
.following .hide_overflow,
.add_and_remove .hide_overflow,
#post_avatar_32873812259, .arrow/*,
.post_info,
.post_controls,
.caption,
.explore_more_tags,
#post_avatar_32873812259,
.footer_links.with_tags*/  {
visibility: hidden !important;
}
.add_and_remove:before {
content:"tumblr - concealr userstyles.org" !important;
font: bold small-caps 1.0em helevetica !important;
margin-top: 0px !important;
}
.forever_alone {
height: 38px !important;
}

LI[class*="notification"],
#tab-ohnethemetest,
#tab-ohnethemestoo {
  display: none !important;
}
}
