@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slingfile.com") {

#downloadtimer .leftcol,
#downloadtimer .timer {background-image: none !important;}

#downloadtimer .leftcol {float: none !important; width: auto !important;}

.adbox1,
.adbox2,
.downloadbar .button,
.plans,
.types .firstcol,
.types .hispeed-btn,
.types > table > tbody > tr:first-child ~ *,


#downloadtimer .rightcol,
#downloadtimer .leftcol > p,
#downloadtimer .leftcol .upgrade,
#downloadtimer .leftcol .wait,

.first,
.first ~ *:not(.box-wrap-single),

.sharelink,
#footer,
#footer ~ *

{display: none !important;}

}
