css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("elblogsalmon.com") {
  body.tabbed { padding:0px !important }
#header { display:none !important }
  .sidewrap {  display:none !important }
  .post-single .post-body { width:auto !important }
#content { width:auto !important }
  .tabbed #content, .tabbed #static { margin:0px !important }
  .container { width:auto !important }
  .header-page {display:none !important }
  .head-container-with-ad {display:none !important }
  .article-social-share {display:none !important }
  .section-article-single .article-summary, .section-article-single .article-taxonomy { width:auto !important }
  .article-author-details{ float:none !important }
  .related-alts{ display:none !important }
#body { width:auto !important }
#favicons-list { display:none !important }
  .aside { display:none !important }
  div#cookies-overlay  { display:none !important }
  p { max-width:none !important }
  .article-author{ display: none !important }
  .article-aside.m-normal-first-aside{ display: none !important }
   .blob { max-width:none !important }
}
