css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("u-cursos.cl") {
ul.modulos li.sel, ul.modulos li.sel:hover {border-color: #0f68ad !important;}
th {background: #dfeff7 !important;color: #666666 !important;}
tr.separador {background-color: #F1F4F8 !important;color: #0f68ad !important;}
h1#ucursos {background-image: url( "http://imageshack.us/a/img193/2242/ucursosmedi.png") !important;border-bottom: 5px solid #0f68ad !important;}
h2.ucursos {border-bottom: 5px solid #0f68ad !important;background-image: url("https://www.u-cursos.cl/medicina/diseno/blanco.png") !important;background-position: top right !important;}
h1.modulo {color: #0f68ad !important;border-bottom: 1px solid #0f68ad !important;}
ul.modulo li {border-right:1px solid #0f68ad !important;}
ul.modulo li a {color: rgb(149, 149, 149) !important;}
ul.modulo li.sel a {background: #0f68ad !important;}
ul.modulo li.sel a {background: none repeat scroll 0% 0% rgb(15, 104, 173) !important;color: white !important;text-decoration: none !important;border-top: 0px solid rgb(225, 12, 18) !important;}
.boton {background-image: url( "https://www.u-cursos.cl/diseno/images/buscar2.png" ) !important;}
a {color: #1090BF !important;}
a:hover {color: #FF0000 !important;}
a, a:link, a:visited, a:active {text-decoration: none;color: black;}
table {background: #fff !important;}
th {background: none repeat scroll 0% 0% rgba(223, 239, 247, 1) !important;color: rgb(102, 102, 102) !important;}
td.verde, td.opciones{background: #f9f4e8 !important;}
td.gris{background: #f4f4f4 !important;}
div#izquierda div h1 {color: rgb(203, 154, 45) !important;background-color: rgb(240, 240, 240) !important;text-align: left;text-transform: uppercase;border-right: 1px solid rgb(224, 224, 224) !important;border-style: none solid solid !important;border-color: rgba(224, 224, 224, 1) !important;border-image: none !important;}
div#izquierda div h1 a {text-decoration: none !important;color: rgb(203, 154, 45) !important;}
div#izquierda div.off h1 a {color: rgb(165, 165, 165) !important;}
div#izquierda div ul li a {color: rgb(133, 133, 133) !important;}
div.nuevo .texto, div.responder .texto {border-color: rgb(114, 203, 247) !important;background-color: rgba(223, 239, 247, 0.6) !important;}
}
