css:
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document url-prefix('https://www.tumblr.com/login') { 

.startpage{
background-color: #0a0a0a; 
background-image: url(http://sadieshihtzu.com/wp-content/uploads/2010/12/Cute-Twitter-background-pink-brown.png) !important; 
background-repeat: repeat !important; 
background-attachment: fixed !important; 
background-position: center; 
}

.sign_up {
background-color: #555 !important;
}

*{font-family: Georgia, Times New Roman, Serif !important; font-style:italic !important; text-shadow:none !important; border:0px !important} 

a, a:visited{color:#666 !important} 

a:hover{color:#333 !important} 

label{color:#666 !important} 

.input_wrapper{opacity:0.5 !important} 

a.login{opacity:0.3 !important; color:#fff !important} 

button{opacity:0.6 !important; color:#fff !important} 
button:hover{opacity:0.8 !important; color:#fff !important} 

.logo {
height: 0 !important;
	width: 0 !important;
	padding-left: 440px !important;
	padding-top: 100px !important;
	background: url(http://sadieshihtzu.com/wp-content/uploads/2010/12/Cute-Twitter-background-pink-brown.png) no-repeat !important; }

}
