@-moz-document domain("tweakers.net") {
body {background-color:#545454; min-height: 100%; position: relative;}
#layout {min-width: 1346px; width: 1346px; margin-right: auto; margin-left: auto; background-color:#fefefe;}
#contentArea{margin: 0 auto; width: 980px; left: 173px;}
#top {min-width: 1346px; width: 1346px;}
#bottom {min-width: 1346px; width: 1346px;}
#menubar {background: linear-gradient(#262828, #1C1E1E) repeat scroll 0 0 #444444; min-width: 1346px; width: 1346px;}
#tweakbase {background: none repeat scroll 0 0 #EDEEEE;}
.trackerPopup {background: none repeat scroll 0 0 #404040;}
.fpaItem div.textContainer {background-color:#444444;}
.fpaItem:hover .imageContainer {opacity:0.85}
.reactie, .articleColumn {line-height: 1.2}
table.highlights td {padding: 3px}
.reactie .topBorder {background: none}
.popup trackerPopup {display: block; width: 252px; left: -272px;}
}
