css:
@-moz-document url('http://forums.d2jsp.org/'), url('http://forums.d2jsp.org/index.php'){

    .bar {
        left: 10px;
        position: fixed;
        top: 0px;
        width: 90%;
        z-index: 1000;
    }

    .head{
        position: relative;
        margin: 30px 0 0 0 !important;
    }

    .lM, .lB{
        position: absolute;
        left: 5px !important;
        top: 2px !important;
    }
    
    body{
        background-color: red !important;
    }

    .lc{
        display: none;
        width: 0 !important;
        height: 0 !important;
    }

    /*
    Remove the last reply since it's useless
    */
    tr th:nth-child(4), tr th:nth-child(3), tr td:nth-child(3){
        display: none;
    }

    TABLE, TR, TD, BODY, SELECT, INPUT, BUTTON, TEXTAREA, th, dt, dd, td a, td div{
        font: 14px Tahoma !important;
    }

}
