css:
@-moz-document domain("zh.wikipedia.org") {
div.mw-parser-output {
  	line-height: 1.5!important;
	zoom: 150%!important;
}

.mwe-math-fallback-image-inline {
	zoom: 100%!important;
}

.mw-editfont-monospace {
	zoom: 150%!important;
	font-family: Consolas!important;
}
}

@-moz-document domain("guokr.com"), domain("guo.kr") {
a.gui-screen-top {
    display: none!important;
}
.gnav-logo-big {
    background: url(http://upload.wikimedia.org/wikipedia/zh/b/b9/Guokr_logo.svg) no-repeat!important;
}
.gnav-logo {
    width: 55px!important;
    height: 35px!important;
    background: url(http://upload.wikimedia.org/wikipedia/zh/b/b9/Guokr_logo.svg) no-repeat!important;
}
.sub-nav-wrap h2 {
    width: 115px!important;
}
.post .post-detail {
    font-size: 20px!important;
}
.gheader-new .gh-wrap {
    font-size: 7pt!important;
}
.gheader-new .gh-search-txt {
    width: 100px;
}
.gheader-new .gh-notice {
    float: left;
}
[class^="icon-"] {
    font-family: guokr-group-icons!important;
}
.icon {
    font-family: guokr-scientific!important;
}
}

@-moz-document domain("twitter.com") {
div.dashboard {
    display: none!important;
}
div.content-main {
    width: auto!important;
}
}

@-moz-document domain("76.164.232.70") {
.t_msgfont {
    font-size: 2em;
}
}

@-moz-document url-prefix("https://www.google.com/calendar/") {
.dr-date {
    width: 80px!important;
    text-align: center;
}
}

@-moz-document url-prefix("http://www.jjwxc.net/onebook.php") {
font {
    display: none;
}
}

@-moz-document url-prefix("http://www.google.com"), url-prefix("https://www.google.com") {
.col {
    width: 120%!important;
}
#cnt #center_col,
#cnt #foot {
    width: 90%!important;
    min-width: 528px!important;
}
#cnt #center_col {
    margin-left: 80px!important;
    zoom: 150%!important;
}
.s {
    max-width: 100%!important;
    width: 100%!important;
    min-width: 100%!important;
    padding-right: 80px!important;
}
}

@-moz-document url-prefix("http://movie.douban.com/") {
div#content {
    margin-left: 5%!important;
}
}

@-moz-document domain("weibo.com") {
.tips_warn,
#WB_webim,
.WB_footer {
    display: none!important;
}
}

@-moz-document url-prefix("http://baike.baidu.com/") {
div.main-content {
    font-family: 微软雅黑;
}
}

@-moz-document url-prefix("https://zh.wikipedia.org/wiki/") {
#mw-content-text p, pre,
div#toc>ul,
#mw-content-text h2,
#mw-content-text h3 {
    zoom: 150%;
    line-height: 1.5em;
}
}
