css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.useit.com") {
body{
  padding: 0;
  margin: 0;
  line-height: 1.5;
  background: #DEDFA7;
}

a:link,a.old:link, a.new:link{color: #900}
a:visited{color: #600;}
a:hover{color: #f00;}

h1,h2,h3,h4{
  font-family: Times New Roman; 
  font-style: italic;
  font-weight: normal;
  color: #900; }
h1,
h2{
  line-height: 1;
  margin: 24px 0 8px; padding: 0 0 2px;
  border-bottom: 2px solid;}
h1{margin-top: 8px;}

.navbar{
  background: #DEDF6F;
}
}
@-moz-document url("http://www.useit.com"),
               url("http://useit.com"){
body>table{width: 650px !important;}
}

@-moz-document url-prefix("http://www.useit.com/alertbox/"),
               url-prefix("http://useit.com/alertbox/") {
.maintext{
  width: 650px;
  margin-bottom: 24px;
  padding: 12px 24px 24px;
  background: #fff;
}
.overline{margin: 0}
  .overline strong{
    font-size: 13px;
    font-weight: normal; 
    color: #666;}
}
