css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("getmusicbee.com") {

/* Color convention
black: #000
dark grey: #222
light grey: #666
orange: rgb(253,196,35)
*/


/* Blocked */

div#footerarea,
#twitterB:link,
a#facebook
    {display: none !important;}


/* Wider */

body
    {width: 100% !important;}


/* Black BG */
.tborder,
headertitles,
.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg a:link,
.titlebg a:visited,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td,
.titlebg2 a:link,
.titlebg2 a:visited
    {background: #000 !important;}


/*  Dark Grey BG */

body,
.headertitles
    {background: #222 !important;}


#bodyarea,
.windowbg,
.windowbg2,
.headerbodies,
td.catbg
    {background-color: #222 !important;}

.catbg,
.catbg3
    {background-image: none !important;}

/* Fonts light grey */
body,
td,
th
    {color: #666 !important;}


/* Fonts links yellow rgb(253,196,35) */
a,
a:link,
#tabs a span
    {color: rgb(253,196,35) !important;}


/*  Borders Black */
.tborder,
.headerbodies
    {border: 1px solid #000 !important;}
    
#headerarea
    {border-bottom: 1px solid #000 !important;}
    
.bordercolor
    {background-color: #000 !important;}

}
